mapper.select:
select count(*)
from final.follow
where memberIdx2 = ${memberIdx};

select count(*)
from final.follow
where memberIdx = ${memberIdx};

select count(*)
from final.photoBoard
where memberIdx = ${memberIdx};

select count(*)
from final.follow
where memberIdx = ${param1} and memberIdx2 = ${param2};

mapper.delete:
delete 
from final.follow
where memberIdx =${param1} and memberIdx2 = ${param2};
